mapper.update:
update tbl_board 
	set group_name=#{group_name}, title=#{title}, content=#{content}, writer=#{writer}, updateDate = sysdate
		where bno=#{bno}

mapper.select:

	select bno, title, writer, content, regdate, updatedate, group_name
	from (
	    select /*+ index_desc(tbl_board pk_board) */
		    rownum rn, bno, title, writer, content, regdate, updatedate, group_name
		    	from tbl_board
		    where 


	    rownum <=#{pageNum} * #{amount}
    )
    where rn> (#{pageNum}-1) * #{amount} and group_name = #{group_name}
