mapper.select:

	select gno, group_name, leader, subject, description, member_number, viewcnt, regdate
		from (
	    select 
	    /*+ index_desc(tbl_group pk_group ) */
		    rownum rn, gno, group_name, leader, subject, description, member_number, authorized, viewcnt, regdate
		    	from tbl_group
		    where 


	   rownum <=#{pageNum} * #{amount} and authorized ='Y'
    )
    where rn> (#{pageNum}-1) *  #{amount}
