mapper.select:

	select qno, title, writer,q_content,reg_date,up_date,pwd,p_group
	from (
	    select /*+ index_desc(qna_tbl pk_qna) */
		    rownum rn, qno, title, writer,q_content,reg_date,up_date,pwd,p_group
		    	from qna_tbl
		    where 



	    rownum <=#{pageNum} * #{amount} 
    )
    where rn> (#{pageNum}-1) * #{amount} order by qno asc

mapper.update:
update qna_tbl set title=#{title},writer=#{writer},q_content=#{q_content},
	up_date=#{up_date},pwd=#{pwd, jdbcType=TIMESTAMP},p_group=#{p_group}
		where qno=#{qno}
